// src/components/UserList.js
import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUsers } from '../features/users/userSlice';
import axios from 'axios';

const UserList = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users.data);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [sort, setSort] = useState('id');
  const [filter, setFilter] = useState({ gender: '', country: '' });

  useEffect(() => {
    dispatch(fetchUsers({ limit: 10, skip: (page - 1) * 10 }));
  }, [dispatch, page]);

  const handleSort = (field) => {
    setSort(field);
    // Logic to sort the data
  };

  const handleFilter = (field, value) => {
    setFilter((prev) => ({ ...prev, [field]: value }));
    // Logic to filter the data
  };

  const loadMore = () => {
    if (hasMore) {
      setPage((prev) => prev + 1);
    }
  };

  return (
    <div>
      <header>
        <img src="https://pixel6.co/wp-content/themes/new-pixel6-wp/assets/images/Pixel6.png" alt="Company Logo" />
        <h1>Employees</h1>
      </header>
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort('id')}>ID</th>
            <th onClick={() => handleSort('name')}>Name</th>
            <th onClick={() => handleSort('age')}>Age</th>
          </tr>
          <tr>
            <th>
              <input type="text" placeholder="Filter by Gender" onChange={(e) => handleFilter('gender', e.target.value)} />
            </th>
            <th>
              <input type="text" placeholder="Filter by Country" onChange={(e) => handleFilter('country', e.target.value)} />
            </th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.age}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={loadMore}>Load More</button>
    </div>
  );
};

export default UserList;
