import React, { useEffect, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import InfiniteScroll from 'react-infinite-scroll-component';
import { fetchUsers } from '../features/users/userSlice';
import './UserList.css';

const UserList = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users.data);
  const hasMore = users.length < 100; // Adjust as necessary

  useEffect(() => {
    if (users.length === 0) {
      dispatch(fetchUsers(10, 0));
    }
  }, [dispatch, users.length]);

  const fetchMoreData = useCallback(() => {
    if (hasMore) {
      dispatch(fetchUsers(10, users.length));
    }
  }, [dispatch, users.length, hasMore]);

  return (
    <div>
      <h1>Employees</h1>
      <InfiniteScroll
        dataLength={users.length}
        next={fetchMoreData}
        hasMore={hasMore}
        loader={<h4>Loading...</h4>}
      >
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Image</th>
              <th>Full Name</th>
              <th>Age</th>
              <th>Designation</th>
              <th>Location</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td><img src={user.image} alt={user.firstName} /></td>
                <td>{user.firstName} {user.lastName}</td>
                <td>{user.age}</td>
                <td>{user.company.title}</td>
                <td>{user.address.city}, {user.address.state}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </InfiniteScroll>
    </div>
  );
};

export default UserList;
