// src/components/UserList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component';
import './UserList.css';

const fetchUsers = async (limit, skip) => {
  const response = await axios.get('https://dummyjson.com/users', {
    params: { limit, skip },
  });
  return response.data.users;
};

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [skip, setSkip] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [sortConfig, setSortConfig] = useState({ key: 'id', direction: 'asc' });
  const [filterConfig, setFilterConfig] = useState({ gender: '', country: '' });

  const fetchMoreData = async () => {
    const newUsers = await fetchUsers(100, skip);
    setUsers([...users, ...newUsers]);
    setSkip(skip + 100);
    if (newUsers.length === 0) {
      setHasMore(false);
    }
  };

  useEffect(() => {
    fetchMoreData();
  }, []);

  const sortUsers = (key) => {
    const direction = sortConfig.direction === 'asc' ? 'desc' : 'asc';
    setSortConfig({ key, direction });
    setUsers((prevUsers) =>
      [...prevUsers].sort((a, b) => {
        if (a[key] < b[key]) return direction === 'asc' ? -1 : 1;
        if (a[key] > b[key]) return direction === 'asc' ? 1 : -1;
        return 0;
      })
    );
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilterConfig((prev) => ({ ...prev, [name]: value }));
  };

  const filteredUsers = users.filter((user) =>
    (filterConfig.gender ? user.gender === filterConfig.gender : true) &&
    (filterConfig.country ? user.address.country === filterConfig.country : true)
  );

  return (
    <div>
      <header>
        <img src="https://pixel6.co/wp-content/themes/new-pixel6-wp/assets/images/Pixel6.png" alt="Company Logo" />
        <h1>Employees</h1>
      </header>
      <div className="filters">
        <label>
          Gender:
          <select name="gender" onChange={handleFilterChange}>
            <option value="">All</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </label>
        <label>
          Country:
          <select name="country" onChange={handleFilterChange}>
            <option value="">All</option>
            <option value="USA">USA</option>
            <option value="Canada">Canada</option>
            <option value="UK">UK</option>
            {/* Add more countries as needed */}
          </select>
        </label>
      </div>
      <InfiniteScroll
        dataLength={users.length}
        next={fetchMoreData}
        hasMore={hasMore}
        loader={<h4>Loading...</h4>}
      >
        <table>
          <thead>
            <tr>
              <th onClick={() => sortUsers('id')}>ID</th>
              <th onClick={() => sortUsers('firstName')}>Name</th>
              <th onClick={() => sortUsers('age')}>Age</th>
              <th>Gender</th>
              <th>Country</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.firstName} {user.lastName}</td>
                <td>{user.age}</td>
                <td>{user.gender}</td>
                <td>{user.address.country}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </InfiniteScroll>
    </div>
  );
};

export default UserList;
